create function f_capital(str varchar(100)) returns varchar(100)
begin

declare v_organiza varchar(100) default "";
declare v_tam int default 0;
declare v_cont int default 0;
declare espaco boolean default false;

set v_tam = length(str);

	while(v_cont<=v_tam) do
    
			if(v_cont = 1 or espaco) then
				set v_organiza = concat(v_organiza, Upper(substring(str,v_cont,1)));
				set espaco = false;
			else
				set v_organiza = concat(v_organiza, Lower(substring(str,v_cont,1)));
			end if;
            
			if(substring(str,v_cont,1) = " " ) then
             set espaco = true;
			end if;
            
		set v_cont = v_cont+1;
        
	end while;

return v_organiza;

end ##
